package org.xbmc.fakemailer.Model;import androidx.room.ColumnInfo;import androidx.room.Entity;import androidx.room.PrimaryKey;@Entity(tableName = "mails")public class Mail {    @PrimaryKey(autoGenerate = true) @ColumnInfo(name="id")    int id;    @ColumnInfo(name="mail")    String mail;    public Mail(String mail){        this.mail = mail;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getMail() {        return mail;    }    public void setMail(String mail) {        this.mail = mail;    }}