package org.xbmc.fakemailer.RoomDb;import androidx.lifecycle.LiveData;import androidx.room.Dao;import androidx.room.Insert;import androidx.room.Query;import org.xbmc.fakemailer.Model.Mail;import java.util.List;@Daopublic interface MailDao {    @Insert    Long insertTask(Mail mail);    @Query ( "UPDATE mails SET mail = :mail  WHERE id=:id " )    int updateTast(int id, String mail);    @Query( "DELETE FROM mails WHERE mail =:mail " )    int deleteTask(String mail);    @Query("SELECT * FROM mails ORDER BY mail DESC")    LiveData<List<Mail>> getAllMail();    @Query("SELECT * FROM mails WHERE mail =:mailsss")    LiveData<List<Mail>> getAllMailOnId(String mailsss);    @Query("SELECT COUNT(mail) FROM mails WHERE mail = :mailsss")    int checkMailExist(String mailsss);}