package org.xbmc.fakemailer.RoomDb;import android.content.Context;import androidx.annotation.NonNull;import androidx.room.Database;import androidx.room.DatabaseConfiguration;import androidx.room.InvalidationTracker;import androidx.room.Room;import androidx.room.RoomDatabase;import androidx.sqlite.db.SupportSQLiteOpenHelper;import org.xbmc.fakemailer.Model.Mail;@Database(entities = {Mail.class}, version = 2, exportSchema = false)public abstract class RoomDataBase extends RoomDatabase {    private String DB_NAME = "mail_db";    public abstract MailDao mailDao();    private static volatile RoomDataBase noteRoomInstance;    @Override    protected SupportSQLiteOpenHelper createOpenHelper(DatabaseConfiguration config) {        return null;    }    @NonNull    @Override    protected InvalidationTracker createInvalidationTracker() {        return null;    }    @Override    public void clearAllTables() {    }    public static RoomDataBase getDatabase(final Context context) {        if (noteRoomInstance == null) {            synchronized (RoomDataBase.class) {                if (noteRoomInstance == null) {                    noteRoomInstance = Room.databaseBuilder ( context.getApplicationContext ( ),                                                              RoomDataBase.class, "mail_database"                    )                            .build ( );                }            }        }        return noteRoomInstance;    }}