package org.xbmc.fakemailer.Utils;import org.xbmc.fakemailer.Model.DataModel;import org.xbmc.fakemailer.Model.MailData;import org.xbmc.fakemailer.Model.MailDomain;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;public interface RestApiService  {    @GET("api/getMessages?")    Call<DataModel> getMails(@Query ( "username" ) String username,                             @Query ( "domain" ) String domain);    @GET("api/fetchMessage?")    Call<MailData> getMailData(@Query ( "username" ) String username,                               @Query ( "domain" ) String domain,                               @Query ( "email_id" ) String email_id);    @GET("api/getDomains")    Call<MailDomain> getMailDomain();}