package org.xbmc.fakemailer.Utils;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import java.util.concurrent.TimeUnit;import okhttp3.OkHttpClient;import okhttp3.logging.HttpLoggingInterceptor;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;public class RetrofitInstance {    private static Retrofit retrofit = null;    private static String URL = "https://tempmailgen.com/";    public static Retrofit getClient() {        OkHttpClient.Builder client = new OkHttpClient.Builder().connectTimeout( 5, TimeUnit.MINUTES).readTimeout( 5, TimeUnit.MINUTES).writeTimeout( 5, TimeUnit.MINUTES).build().newBuilder();        HttpLoggingInterceptor loggingInterceptor = new HttpLoggingInterceptor();        loggingInterceptor.setLevel(HttpLoggingInterceptor.Level.BODY);        Gson gson = new GsonBuilder ()                .setLenient()                .create();        client.addInterceptor(loggingInterceptor);        retrofit = new Retrofit.Builder()                .baseUrl(URL)                .client(client.build())                .addConverterFactory(GsonConverterFactory.create(gson))                .build();        return retrofit;    }}